#include "leetcode_daily.h"

/**
 * 3043. Find the Length of the Longest Common Prefix
 *
 * Constraints:
 * 	1 <= arr1.length, arr2.length <= 5 * 10^4
 * 	1 <= arr1[i], arr2[i] <= 10^8
 */
int longestCommonPrefix(int *arr1, int arr1Size, int *arr2, int arr2Size);

struct s_input
{
	Array arr1;
	Array arr2;
	int expected;
};

int ft_do_test(struct s_input *input)
{
	int result;
	int check_val;

	ft_print_int_tab(input->arr1.arr, input->arr1.size, NULL);
	ft_print_int_tab(input->arr2.arr, input->arr2.size, NULL);

	result = longestCommonPrefix(input->arr1.arr, input->arr1.size,
								 input->arr2.arr, input->arr2.size);
	check_val = (input->expected == result);
	if (!check_val)
		printf("got \"%d\" whilst \"%d\" was to be expected\n",
			   result, input->expected);
	check(check_val);
	return (0);
}

